import React from 'react';
import { Bar } from 'react-chartjs-2';
import Chart from 'chart.js/auto';
import inventory from './Variables'

const data = {
labels : inventory.map(item => item.name),

datasets : [
{
    label: 'Inventory Percentage',
    backgroundColor: 'rgba(240, 0, 0, 0.3)',
    borderColor: 'rgba(240, 0, 0, 1)',
    borderWidth: 1,
    hoverBackgroundColor: 'rgba(128, 128, 128, 0.5)',
    data: inventory.map(item => (item.qty / item.capacity) * 100),
  },
],
};

 const options = {
    indexAxis: 'y',
    plugins: {
        legend: {
          display: false
        }
    },
};

const customTooltip = tooltipModel => {
  const item = inventory[tooltipModel.dataIndex];
  const tooltipText = `Qty: ${item.qty}, Capacity: ${item.capacity}`;
  return tooltipText;
};

const BarGraph = () => {
    return (
      <div>
        <Bar options={options} data={data} plugins={[{ afterDraw: customTooltip }]} />
      </div>
    );
  };

  export default BarGraph;
